import GuestService from '../services/GuestService.js'
import {eventBus} from '../main.js'

export default {
  name:'guest-form',
  data(){
    return{
      name:'',
      email:'',
      checkedIn: null
    }
  },
  methods:{
    addGuest: function(e) {
      e.preventDefault()
      const guest = {
        name: this.name,
        email: this.email,
        checkedIn: this.checkedIn
      }
      GuestService.postGuest(guest)
      .then(res => eventBus.$emit('guest-added', res))
    }
  }

}
